import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filter, setFilter] = useState("2020");
  const [filterInfoText, setFilterInfoText] = useState("2019, 2021 & 2022");

  const selectFilterHandler = (selectedFilter) => {
    setFilter(selectedFilter);
    console.log(filter);
    if (selectedFilter === '2019') {
      setFilterInfoText("2020, 2021 & 2022");
    } else if (selectedFilter === '2020') {
      setFilterInfoText("2019, 2021 & 2022");
    } else if (selectedFilter === '2021') {
      setFilterInfoText("2019, 2020, 2022");
    } else {
      setFilterInfoText("2019, 2020 & 2021");
    }
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filter}
          onSelectFilter={selectFilterHandler}
        />
        <p>Data for years {filterInfoText} is hidden!</p>
        <ExpenseItem expense={props.expenses[0]} />
        <ExpenseItem expense={props.expenses[1]} />
        <ExpenseItem expense={props.expenses[2]} />
        <ExpenseItem expense={props.expenses[3]} />
      </Card>
    </div>
  );
};

export default Expenses;
