      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={() => changeHandler()}>
          <option value='2022' onSelect={() => clickHandler('2022')}>2022</option>
          <option value='2021' onClick={() => clickHandler('2021')}>2021</option>
          <option value='2020' onClick={() => clickHandler('2020')}>2020</option>
          <option value='2019' onClick={() => clickHandler('2019')}>2019</option>
        </select>
      </div>
    </div>
  );
};

